protected override async Task OnInitializedAsync(){
        if(entry.Text != null){
            try{
                if(!int.TryParse(entry.Text, out DataRetrieval.HomePageSearchId)){
                    DataRetrieval.HomePageSearchString = entry.Text; 
                }
            }catch(Exception){
                Console.WriteLine("Inproper Entry");
            }
