            if(data.TestStatus == 1){
                numberPassed++;
            }
    }
    if(pieSet.Data.Count > 0){
        pieSet.Data.Clear();
    }
    pieSet.Data.AddRange(new double[] { numberPassed, testdata.Length - numberPassed });
    _config.Data.Datasets.Add(pieSet);
    }

